mapper.select:
select f.food_name as foodName, f.food_price as foodPrice, f.description as foodDescription, f.is_recommend as isRecommend, f.today_sell as todaySell, s.window_name as windowName from food f inner join seller s on s.seller_id = f.seller_id where f.is_delete = 0
and food_name like concat('%', #{foodName}, '%')
and food_price = #{foodPrice}
limit #{pageNum}, #{pageSize}

select count(*) from food f inner join seller s on s.seller_id = f.seller_id where f.is_delete = 0
and food_name like concat('%', #{foodName}, '%')
and food_price = #{foodPrice}
