mapper.update:
update food set food_name = #{foodInfoDTO.foodName}, food_price = #{foodInfoDTO.foodPrice},
                description = #{foodInfoDTO.description}, is_recommend = #{foodInfoDTO.isRecommend}
    where food_id = #{foodInfoDTO.foodId}

mapper.select:
select f.food_id as foodId, f.food_name as foodName, f.food_price as foodPrice, f.description, f.is_recommend as isRecommend, f.today_sell as todaySell from food f where f.seller_id = #{sellerId} and f.is_delete = 0
and f.food_name like concat('%' + #{foodName} + '%')
and f.food_price = #{foodPrice}
limit #{pageNum}, #{pageSize}
