mapper.update:
update food set food_name = #{foodEditInfoDTO.foodsEditFoodName}, food_price = #{foodEditInfoDTO.foodsEditFoodPrice},
                description = #{foodEditInfoDTO.foodsEditFoodDescription}, is_recommend = #{foodEditInfoDTO.foodsEditFoodIsRecommend}
    where food_id = #{foodEditInfoDTO.foodId}

mapper.select:
select f.food_id as foodId, f.food_name as foodName, f.food_price as foodPrice, f.description, f.is_recommend as isRecommend, f.today_sell as todaySell from food f where f.seller_id = #{sellerId} and f.is_delete = 0
AND food_name Like concat('%',#{foodName},'%')
and food_price = #{foodPrice}
limit #{pageNum}, #{pageSize}

select count(*) from food f where f.seller_id = #{sellerId} and f.is_delete = 0
and food_name like concat('%', #{foodName}, '%')
and food_price = #{foodPrice}
